import React from 'react';
import PropTypes from 'prop-types';

import './Pagination.scss';


Pagination.propTypes = {
  currentPage: PropTypes.number.isRequired,
}

function Pagination({paginate, currentPage}) {

  const renderButton = () => {
    if (currentPage === 1) return <span className="Pagination__next" onClick={() => paginate(currentPage + 1)}> Next</span>
    return (
      <React.Fragment> 
        <span className="Pagination__prev" onClick={() => paginate(currentPage - 1)}> Previous</span>
        <span className="Pagination__next" onClick={() => paginate(currentPage + 1)}> Next</span>
      </React.Fragment>
    )
  }

  return (
    <div className="Pagination">
        {renderButton()}
    </div>
  );
}

export default Pagination;
