import React from 'react';
import PropTypes from 'prop-types';

import './Table.scss';

Table.propTypes = {
    listIssues: PropTypes.array.isRequired,
    loading: PropTypes.bool.isRequired
}

function Table({listIssues, loading}) {

  const renderTable = () => {
    console.log("LIST", listIssues)
    return (
        <table> 
            <thead class="table-hover"> 
              <tr>
                <th> ID </th>
                <th> Title </th>
              </tr>
            </thead>
            <tbody>
              {listIssues.map((item, idx) => {
                return (
                  <tr key={idx}>
                    <td> {item.id} </td>
                    <td> {item.title} </td>
                  </tr>  
                )
              })}
            </tbody>
        </table>
    )
  }

  return (
    <div className="Table">
        {loading ? (<div className="loading"></div>) : (renderTable())}
    </div>
  );
}

export default Table;
