import { ADD_ITEM } from "../constants/actions-type";

const initialState = {
    listVisitedIssues: []
};

const handleAddItem = (item) => {
    const {listVisitedIssues} = initialState || {}
    if (listVisitedIssues.length === 5) {
        listVisitedIssues.shift()
        listVisitedIssues.push(item)
        return listVisitedIssues
    }
    listVisitedIssues.push(item)
    return listVisitedIssues
}

function rootReducer(state = initialState, action) {
    if (action.type === ADD_ITEM) {
        const listItems = handleAddItem(action.payload)
        console.log("ADD REDUX", listItems)
        state.listVisitedIssues = [...listItems.listVisitedIssues]
        return state
    }
    return state;
}

export default rootReducer;
