import { ADD_ARTICLE } from "../constants/actions-type";

const initialState = {
    articles: ["1"]
  };

function rootReducer(state = initialState, action) {
    if (action.type === ADD_ARTICLE) {
        state.articles.push(action.payload);
    }
    return state;
}

export default rootReducer;
