import React from 'react';
import PropTypes from 'prop-types';

import './Table.scss';

Table.propTypes = {
    listIssues: PropTypes.array.isRequired,
    loading: PropTypes.bool.isRequired
}

const test =  []

const border = {
  border: '1px solid #999999'
}

function Table({listIssues, loading}) {

  const renderContentTable = () => {
    console.log("LIST", listIssues)
    return (
      test.map((item, idx) => {
        return (
          <tr key={idx} className={idx === 4 ? "active" : ""}>
            <td> {item.id} </td>
            <td> {item.title} </td>
          </tr>  
        )
      })
    )
  }

  return (
    <div className="Table" style={{ border: !loading ? border : 'none'}}>
        <table> 
            <thead className="Table__table-hover"> 
              <tr>
                <th> ID </th>
                <th> Title </th>
              </tr>
            </thead>
            <tbody>
              {renderContentTable()}
            </tbody>
        </table>
        {!loading ? (<span className="loading-content-table"></span>) : null}
    </div>
  );
}

export default Table;
